import axios from 'axios';
import moment from 'moment';
import React, { useState } from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useLocation } from 'react-router-dom';


export default function Write() {
  
  const upload = async()=>{
    try{
      const formData=new FormData();
      formData.append("file",file);
      const response =await axios.post('http://localhost:8000/api/upload',formData);
      
      return response.data
    }catch(err){

    }
  }
  const handleClick = async e=>{
    e.preventDefault();
   const imgurl= upload();
   try{
      state ? await axios.put(`/posts/${state.id}` ,{
        title,
        desc:value,
        cat,
        img:file ?imgurl:""
      }) : await axios.post(`/posts/`,{
        title,
        desc:value,
        cat,
        img:file ? imgurl:"",
        date :moment(Date.now()).format("YYYY-MM-DD HH:mm:ss")
      })
   }catch(err){
    console.log(err);
   }
  }
  const state=useLocation.state;
  const[value,setValue] =useState(state?.desc || "");
  const[title,setTitle] =useState(state?.title || "");
  const[file,setFile] =useState(null);
  const[cat,setCat] =useState(state?.cat || "");
  console.log(value);
  return (
    <div className='add'>
      <div className="content">
        <input type="text" value={title} placeholder='Title' onChange={e=>setTitle(e.target.value)}/>
        <div className="editorContainer">
          <ReactQuill className="editor" theme="snow" value={value} onChange={setValue}/>
        </div>
      </div>
      <div className="menu">
        
        
        <div className="item">
            <h1>publish</h1>
            <span>
              <b> Status</b> Draft
            </span>
            <span>
              <b>Visiblity : </b>Public
            </span>
           <input style={{display:'none'}} type='file' id="file" onChange={e=>setFile(e.target.files[0])}/>
           <label className='file' htmlFor='file'>Upload File</label> 
           <div className="buttons">
           <button>Save as a Dreft</button>
            <button onClick={handleClick}>Publish</button>
           </div>
           
        </div>
        
        
        <div className="item">
          <h1> Category</h1>
          <div className="cat">
          <input type="radio" checked={cat  ===  "art"} name="cat" value="art" id='art' onChange={e=>setCat(e.target.value)}/>
          <label htmlFor='art'>Art</label>
          </div>
          
          <div className="cat">
          <input type="radio" checked={cat === "science"} name="cat" value="science" id='science' onChange={e=>setCat(e.target.value)}/>
          <label htmlFor='science'>Science</label>
          </div>
          
          <div className="cat">
          <input type="radio" checked={cat === "technology"} name="cat" value="technology" id='technology' onChange={e=>setCat(e.target.value)}/>
          <label htmlFor='technology'>Technology</label>
          </div>

          <div className="cat">
          <input type="radio"  checked={cat === "cinema"}name="cat" value="cinema" id='cinema' onChange={e=>setCat(e.target.value)}/>
          <label htmlFor='cinema'>Cinema</label>
          </div>

          <div className="cat">
          <input type="radio" checked={cat === "design"} name="cat" value="design" id='design' onChange={e=>setCat(e.target.value)}/>
          <label htmlFor='design'>Design</label>
          </div>

          <div className="cat">
          <input type="radio" checked={cat === "food"} name="cat" value="food" id='food' onChange={e=>setCat(e.target.value)} checked={cat==="art"}/>
          <label htmlFor='food'>Food</label>
          </div>

        </div>
      </div>
    </div>
  )
}
