export default function Home() {
  return (
    <div className='home'>
        <div className="posts">
          {posts.map(posts=>(
            <div className='post' key={posts.id}>
              <div className="img">
                <img src={posts.img} alt="be happy" />
              </div>
              <div className="content">
                <Link className="link" to={`/post/{posts.id}`}>
                  <h1>{posts.title}</h1>
                </Link>
                <p> {posts.desc}</p>
                <button>Read More</button>
              </div>
            </div>
          ))}
        </div>
    </div>
  )
}
